shaders.vertex:
layout(location = 0) in vec2 position;
out vec2 texCoord0;

void main() {
    gl_Position = vec4(position * 2. - 1., 0, 1);
    texCoord0 = position;
}

shaders.fragment:
uniform vec4 color;
uniform sampler2D tex;
in vec2 texCoord0;
out vec4 fragColor;

void main() {
    // fragColor = vec4(texture(tex, texCoord0).rgb, 1);
    fragColor = texture(tex, texCoord0);
